
fn main() {
    let message = &mut "
".chars().collect::<Vec<char>>();
    let message_windows = message.windows(14);
    let mut cnt = 13;

    for window in message_windows {
        let mut w = window.to_vec();
        cnt+=1;
        w.sort();
        w.dedup();
        if w.len() == window.len(){
            println!("Char {}, sequence: {:?}", cnt, window);
            break;
        }
    }
}
